import { useSelector, useDispatch } from "react-redux";
import React from "react";
import { setPage } from "../../redux/action";
import style from './Paginate.module.css';

const Paginate = () => {
  const dispatch = useDispatch();
  const dogs = useSelector(state => state.allDogs);
  const currentPage = useSelector(state => state.page);
  const elementsPerPage = useSelector(state => state.elementsForPage);

  const cards = Math.ceil(dogs.length / elementsPerPage);
  const page = [];

  for (let i = 0; i < cards; i++) {
    page.push(i + 1);
  }

  const onChange = (pageNumber) => {
    dispatch(setPage(pageNumber));
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      onChange(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < cards) {
      onChange(currentPage + 1);
    }
  };

  return (
    <div className={style.pagination}>
      <button
        onClick={handlePreviousPage}
        disabled={currentPage === 1}
      >
        Anterior
      </button>
      <ul className={style.numPages}>
        {page.map((pageNumber, i) => (
          <li
            key={i}
            onClick={() => onChange(pageNumber)}
            className={pageNumber === currentPage ? style.currentPage : ""}
          >
            {pageNumber}
          </li>
        ))}
      </ul>
      <button
        onClick={handleNextPage}
        disabled={currentPage === cards}
      >
        Siguiente
      </button>
    </div>
  );
}

export default Paginate;
