      dispatch(searchById(id))
  }, [id]);


  return (
    <div >
      {<Nav/>}
        <Routes>
          <Route path='/dogs' element={<Card/>} />
          <Route path='/dogs/:id' element={<Detail/>}/>
        </Routes>
      <Cards/>
    </div>
  );
}

export default App;
