import { Link, useParams } from "react-router-dom"
import { useEffect } from "react"
import style from './Detail.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { searchById } from '../../redux/action';




const Detail = () => {
    const { id } = useParams();
    let dogById = useSelector(state => state.DogsById)
        if(dogById.length) {
        dogById = dogById[0]
    }

    const dispatch = useDispatch()


    useEffect(() => {
        dispatch(searchById(id))
    });

    return (
    <div className={style.cardDisplay}>
        <div>
        <Link to={'/'}> <button className={style.button}> HomePage </button> </Link>

        <Link to='/dogs/saveDog'> <button className={style.button}> Create Your Dog </button> </Link>

        <Link to='/dogs/'> <button className={style.button}>All Dogs</button> </Link>

                <h2 className={style.text}>{dogById.name}</h2>
                <img className={style.image} src={dogById.image} alt=''/>
                <h4 className={style.text}>Weight: {dogById.weight}</h4>
                <h4 className={style.text}>Height: {dogById.height}</h4>
                <h4 className={style.text}>Life span: { dogById.life_span}</h4>
                <h4 className={style.temperament}>{dogById.Temperaments? dogById.Temperaments.map((temp) => temp.temperament): dogById.temperament}</h4>
        </div>
    </div>
    )
}

export default Detail
