import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getByName, getByTemperament } from "../../redux/action";
import { useLocation, useNavigate, useParams, Link } from "react-router-dom";
import SearchBar from "../SearchBar/SearchBar";
import Card from "../Card/Card";
import style from '../Nav/Nav.module.css'

 const Nav = () => {

    const dispatch = useDispatch();
    const location = useLocation()
    const navigate = useNavigate()
    const dogByName = useSelector(state => state.DogsByName)
    const temperaments = useSelector(state => state.DogsByTemperament)        
    const { name } = useParams();

    const [aux, setAux] = useState(false);

    const handleOrder = (event) => {
        dispatch(orderCards(event.target.value))
        setAux(true)
    }

    const handleFilter = (event) => {
        dispatch(filterCards(event.target.value))
    }

    useEffect(() => {
        dispatch(getByTemperament())
        if(name)
        dispatch(getByName(name))
        //console.log(name);

    }, [])

    const handleSearch = (searchQuery) => {
        dispatch(getByName(searchQuery))
        && navigate('/dogs/search')
      };

    return(

        <nav>

        <select onChange={han}>

            <option value="A">Ascendente</option>
            <option value="D">Descendente</option>

        </select>

        <select>
          {temperaments.map(({temperament, id}) => (
            <option key={id} value={temperament}>{temperament}</option>
          ))}
        </select>


         <button className={style.logOut}>
         <Link className={style.color} to={'/'}>LogOut</Link>   
         </button>

        <button className={style.about}>
         <Link to='/about'>About</Link>
        </button>

        <button className={style.home}>
         <Link to='/home'>Home</Link>
        </button>

        <button>
         <Link to='/favorites'>Favorites</Link>
        </button>

            <SearchBar onSearch={handleSearch}/>
            <div className={style.cardDisplay}>
         {  location.pathname === '/dogs/search' &&
            dogByName.map(({id, name, temperament, life_span, weight, height, image}) => {
               return (
                  <Card
                    key={id}
                    id={id}
                    name={name}
                    temperament={temperament}                     
                    life_span={life_span}
                    weight={weight? weight: weight.metric}
                    height={height? height: height.metric}
                    image={image.url? image.url: image}
                  />
               )
            })
         }
      </div>


        </nav>

    )

}

export default Nav;
