import { useParams } from "react-router-dom"
import { useEffect } from "react"
//import style from './Detail.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { searchById } from '../../redux/action';




const Detail = () => {
    const { id } = useParams();
    let dogById = useSelector(state => state.DogsById)
        if(dogById.length) {
        dogById = dogById[0]
    }

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(searchById(id))
    }, [id]);

    return (
    <div>
        <div className="Detail">
            <card>
                <h2>{dogById.name}</h2>
                <img src={dogById? dogById.image: dogById.image.url} alt=''/>
                <h4>{dogById? dogById.weight.metric: dogById.weight}</h4>
                <h4>{dogById? dogById.height: dogById.height.metric}</h4>
                <h4>{dogById.temperament}</h4>
                <h4>{dogById.life_span}</h4>
                <h4>{}</h4>
            </card>
        </div>
    </div>
    )
}

export default Detail
