import { useEffect, useState } from "react"
//import style from './Detail.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { getByName } from '../../redux/action';


export default function SearchBar({onSearch}) {
   console.log(onSearch);
   const [name, setName] = useState("");

   const handleChange = (event) => {
      setName(event.target.value)
   }

   return (

      <div>
         <input type='search' onChange={handleChange} value={name}/>
            <button onClick={() => {onSearch(name); setName("")}}>Buscar</button>
               <div className={style.cardDisplay}>
               {
                  dogs.map(({id, name, temperament, life_span, weight, height, image}) => {
                     return (
                        <Card
                          key={id}
                          id={id}
                          name={name}
                          temperament={temperament}                     
                          life_span={life_span}
                          weight={weight? weight: weight.metric}
                          height={height? height: height.metric}
                          image={image.url? image.url: image}
                        />
                     )
                  })
               }
         </div>
      </div>
   );

}
