import { useSelector, useDispatch } from "react-redux";
import React from "react";
import { paginate } from "../../redux/action";
import style from './Paginate.module.css';

const Paginate = () => {
  const dispatch = useDispatch();
  const dogs = useSelector(state => state.allDogs);
  const numberPage = useSelector(state => state.page);
  const elements = useSelector(state => state.elementsForPage);

  const cards = Math.ceil(dogs.length / elements);
  const page = [];

  for (let i = 0; i < cards; i++) {
    page.push(i + 1);
  }

  const onChange = (pageNumber) => {
    dispatch(paginate(pageNumber));
  };

  return (
    <ul className={style.numPages}>
      {page.map((pageNumber, i) => (
        <li
          key={i}
          onClick={() => onChange(pageNumber)}
          className={pageNumber === numberPage ? style.currentPage : ""}
        >
          {pageNumber}
        </li>
      ))}
    </ul>
  );
}

export default Paginate;
