import { Link } from "react-router-dom"
import style from "./Card.module.css"
import {connect} from 'react-redux'
import {getByBreed, searchById} from "../../redux/action"
import { useState, useEffect } from "react"

const Card = ({id, image, name, heigth, weigth, temperament, lifeSpan, onClose }) => {

    return (
        <div>
            <card>
                <button onClick={() => onClose(id)}>X</button>
                <img src={image} alt='' />
                <Link to={`/detail/${id}`}>
                    <h2 className={style.text}>{name}</h2>
                </Link>            </card>
        </div>

        )
    }

    const mapStateToProps = (state) => {
        return {
           allDogs: state.allDogs
        }
     }

     const mapDispatchToProps = (dispatch) => {
        return {
            getByBreed: () => { dispatch(getByBreed()) },
            searchById: (id) => { dispatch(searchById(id)) }
        }
     }

export default connect(mapStateToProps, mapDispatchToProps)(Card)
