import { useParams } from "react-router-dom"
import { useEffect } from "react"
//import style from './Detail.module.css'
import { useDispatch, useSelector } from 'react-redux';
import { searchById } from '../../redux/action';




const Detail = () => {
    const { id } = useParams();
    let dogById = useSelector(state => state.DogsById)
        if(dogById.length) {
        dogById = dogById[0]
    } else {
        dogById = {
            name: dogById.name,
            image: dogById.image.url,
            weight: dogById.weight.metric,
            height: dogById.height.metric,
            id: dogById.id,
            temperament


        }

    }

    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(searchById(id))
    }, [id]);

    return (
    <div>
        <div className="Detail">
            <card>
                <h2>{dogById.name}</h2>
                <img src={dogById.image.url? dogById.image.url: dogById.image} alt=''/>
                <h4>{dogById.weigth.metric? dogById.weight.metric: dogById.weight}</h4>
                <h4>{dogById.height.metric? dogById.height.metric: dogById.height}</h4>
                <h4>{dogById.temperament}</h4>
                <h4>{dogById.life_span}</h4>
                <h4>{}</h4>
            </card>
        </div>
    </div>
    )
}

export default Detail
